import React, { Component } from 'react';
import Slider from 'infinite-react-carousel';


class Carousel extends Component {
    render(){
        const settings = {
            arrows: false,
            dots: true
        }
        const handleClickProducts = page => {
            window.location.href="/" + page
        }
        return(
            <Slider { ...settings }>
                <div>
                    <img src="https://via.placeholder.com/300" alt="placeholder1" />
                    <button onClick="/products">View Products</button>
                </div>
                <div>
                    <img src="https://via.placeholder.com/300" alt="placeholder2" />
                    <button onClick="/cart">View Cart</button>
                </div>
                <div>
                    <img src="https://via.placeholder.com/300" alt="placeholder3" />
                    <button onClick="/contact">Find Us</button>
                </div>
            </Slider>
        )
    }
}
export default Carousel;
